'use strict'
module.exports = (sequelize, DataTypes) => {
  /**
   * An association between users and their (ordered) favorite content lists
   * Favorites accepts any Array of type String so that content lists with
   * natural blockchain ids can be stored alongside autogenerated content lists.
   */
  const UserContentListFavorites = sequelize.define('UserContentListFavorites', {
    userId: {
      type: DataTypes.INTEGER,
      allowNull: false,
      primaryKey: true
    },
    favorites: {
      type: DataTypes.ARRAY(DataTypes.STRING),
      allowNull: false
    },
    createdAt: {
      allowNull: false,
      type: DataTypes.DATE
    },
    updatedAt: {
      allowNull: false,
      type: DataTypes.DATE
    }
  }, {})
  return UserContentListFavorites
}
